import React, { useState, useEffect } from "react";
import axios from "axios";
import "../Styles/Recip.css"; // Import your styles

const dummyRecipes = [
  {
    id: 1,
    name: "Spaghetti Bolognese",
    image: "data:image/jpeg;base64,",
    ingredients: ["Spaghetti", "Tomato Sauce", "Ground Beef"],
    instructions: "Cook spaghetti, saut√© beef, add sauce, mix and serve.",
  },
  {
    id: 2,
    name: "Chicken Curry",
    image:"data:image/jpeg;base64,",
    ingredients: ["Chicken", "Coconut Milk", "Spices"],
    instructions: "Cook chicken, add spices, pour coconut milk, simmer.",
  },
  {
    id: 3,
    name: "Avocado Toast",
    image: "data:image/jpeg;base64,",
    ingredients: ["Bread", "Avocado", "Egg"],
    instructions: "Toast bread, mash avocado, add egg on top.",
  },
];

const ExploreRecipes = () => {
  const [recipes, setRecipes] = useState([]); // Store recipes
  const [search, setSearch] = useState(""); // Store search input
  const [selectedRecipe, setSelectedRecipe] = useState(null); // Store selected recipe

  // Fetch recipes from API when component mounts
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/recipes");
        setRecipes(response.data);
      } catch (error) {
        console.error("Error fetching recipes:", error);
        setRecipes(dummyRecipes); // Use dummy data as fallback
      }
    };

    fetchRecipes();
  }, []);

  // Filter recipes based on search input
  const filteredRecipes = recipes.filter((recipe) =>
    recipe.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="explore-container">
      <h1>Explore Recipes</h1>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search recipes..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search-bar"
      />

      {/* Recipe Grid */}
      <div className="recipe-grid">
        {filteredRecipes.length > 0 ? (
          filteredRecipes.map((recipe) => (
            <div
              key={recipe.id}
              className="recipe-card"
              onClick={() => setSelectedRecipe(recipe)}
            >
              <img src={recipe.image} alt={recipe.name} className="recipe-image" />
              <h3>{recipe.name}</h3>
              <button className="view-button">View Recipe</button>
            </div>
          ))
        ) : (
          <p className="no-results">No recipes found.</p>
        )}
      </div>

      {/* Recipe Modal */}
      {selectedRecipe && (
        <div className="modal-container show">
          <div className="modal-content">
            <button className="close-button" onClick={() => setSelectedRecipe(null)}>X</button>
            <h2>{selectedRecipe.name}</h2>
            <img src={selectedRecipe.image} alt={selectedRecipe.name} className="recipe-image" />
            <h3>Ingredients</h3>
            <ul>
              {selectedRecipe.ingredients.map((ingredient, index) => (
                <li key={index}>{ingredient}</li>
              ))}
            </ul>
            <h3>Instructions</h3>
            <p>{selectedRecipe.instructions}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default ExploreRecipes;
